#include <stdint.h>

int main() {

    // Postiv und Negativ Ganzezahlen
    int8_t zahl; // Byte 8
    int16_t zahl1; // Byte 16
    int32_t zahl2; // Byte 32
    int64_t zahl3; // Byte 64

    //Postive Ganzezahlen

    uint8_t zahl4; //Byte 8
    uint16_t zahl5; // Byte 16
    uint32_t zahl6; // Byte 32
    uint64_t zahl7; // Byte 64

    return 0;

}

