#[actix_web::get("/")]
async fn hallo() -> impl    actix_web::Responder {
    actix_web::HttpResponse::Ok().body("Hallo Welt")
}

#[actix_web::main]
async fn main() -> std::io::Result<()> {
    actix_web::HttpServer::new(|| {
        actix_web::App::new()
            .service(hallo)

    })
        .bind(("127.0.0.1", 8080))?
        .run()
        .await
}

