* http://localhost:8080
<pre>
#[actix_web::get("/")]
async fn hallo() -> impl    actix_web::Responder {
    actix_web::HttpResponse::Ok().body("Hallo Welt")
}

#[actix_web::main]
async fn main() -> std::io::Result<()> {
    actix_web::HttpServer::new(|| {
        actix_web::App::new()
            .service(hallo)

    })
        .bind(("127.0.0.1", 8080))?
        .run()
        .await
}

</pre>

==Hallo Welt auf Port 8080
<pre>
package main

import "net/http"

func main() {

	var server = http.NewServeMux()
	server.HandleFunc("/", Hallo)
	http.ListenAndServe("localhost:8080", server)
}

func Hallo(w http.ResponseWriter, r *http.Request) {
	w.Write([]byte("Hallo Welt"))
}
</pre>

