==Cookie setzen==
<pre>
#include <fcgi_stdio.h>
#include <stdlib.h>
void test();
int main() {
    while (FCGI_Accept() >= 0)
        test();
    return 0;
}

void test() {
    printf("Set-Cookie: cookieName=thorsten\r\n");
    printf("Content-type: text/html\r\n");
    printf("\r\n");
    printf("<html>");
    if (getenv("HTTP_COOKIE") !=NULL){
        printf("%s\n", getenv("HTTP_COOKIE"));
    } else {
        printf("Cookie ist nicht gesetzt\n");
    }
}

</pre>

